import { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addContact } from '../Redux/action';

function ContactForm() {
    const dispatch = useDispatch();

    const [form, setForm] = useState({
        first_name: '',
        last_name: '',
        mob: '',
        status: 'active',
    });

    const handleChange = (e) => {
        setForm({
            ...form,
            [e.target.name]: e.target.value,
        });
    };

    function handleSave() {
        dispatch(addContact(form));
    }

    return (
        <div className="w-full sm:w-1/2 md:w-1/3 lg:w-1/4 xl:w-1/5 mx-auto my-4 pt-16">
            <h2 className="text-2xl font-bold text-white mb-4">Create Contact</h2>
            <div className="bg-gray-800 rounded-lg p-4 sm:p-6 shadow-md mb-4">
                {/* ... Other form elements */}
            </div>
            {/* ... Other form elements */}
            <button
                className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                onClick={handleSave}
            >
                Save Contact
            </button>
        </div>
    );
}

export default ContactForm;
