import { useLocation } from "react-router-dom";
import './App.css';
import SideBar from './Components/SideBar';
import AllRoutes from './Pages/AllRoutes';
import Logo from './utils/logo.png'; // Replace './logo.png' with the actual path to your image file

// import {Logo} from '../ScreenShots/logo.png';

function App() {

  const location = useLocation();
  const currentRoute = location.pathname;

  return (
    <div className="App">
      <div className="z-50 w-full fixed shadow-sm shadow-slate-700 top-0 text-2xl text-black bg-white font-bold p-4'"><img  src={Logo} cla alt="logo" /></div>

      {/* <h1 className='z-50 w-full fixed shadow-sm shadow-slate-700 top-0 text-2xl text-black bg-white font-bold p-4'>
        {
          currentRoute == "/" ? ' Contact Management App' : "Charts and Maps"
        }
      </h1> */}
      <div className='flex w-full '>
        <div className='sticky  top-0 h-screen'>
          <SideBar />
        </div>
        <div className='w-full'>
          <AllRoutes />
        </div>
      </div>
    </div>
  );
}

export default App;
