import axios from "axios";
import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import { MapContainer, TileLayer } from "react-leaflet";
import WorldMap from "../Components/WorldMap";
import 'leaflet/dist/leaflet.css';

const Dashboard = () => {
  const [countriesData, setCountriesData] = useState([]);
  const [chartData, setChartData] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://disease.sh/v3/covid-19/countries"
        );
        setCountriesData(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const fetchChartData = async () => {
      try {
        const response = await axios.get(
          "https://disease.sh/v3/covid-19/historical/all?lastdays=all"
        );
        const data = response.data;
        const newChartData = {
          labels: Object.keys(data.cases),
          datasets: [
            {
              label: "Cases",
              data: Object.values(data.cases),
              fill: false,
              borderColor: "#f50057",
              tension: 0.2,
            },
          ],
        };
        setChartData(newChartData);
      } catch (error) {
        console.error("Error fetching chart data:", error);
      }
    };

    fetchChartData();
  }, []);

  return (
    <div className="bg-gray-200 min-h-screen py-8">
      <div className="container mx-auto p-4">
        <h1 className="text-4xl font-bold mb-8 text-pink-600">Corona Cases Chart</h1>
        <div className="bg-white rounded-lg shadow-lg p-4">
          {chartData ? (
            <Line data={chartData} />
          ) : (
            <h1 className="text-pink-600 mb-4 font-bold text-2xl">Loading...</h1>
          )}
        </div>

        <h1 className="text-4xl font-bold mb-8 mt-8 text-blue-500">Corona Cases World Map</h1>
        <div className="bg-white rounded-lg shadow-lg p-4">
          <MapContainer
            className="w-full h-96"
            bounds={[[-60, -180], [85, 180]]}
            zoom={2}
            center={[20, 40]}
            scrollWheelZoom={true}
          >
            <TileLayer
              url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
              attribution='&copy; <a href="https://stadiamaps.com/">Stadia Maps</a>, &copy; <a href="https://openmaptiles.org/">OpenMapTiles</a> &copy; <a href="http://openstreetmap.org">OpenStreetMap</a> contributors'
            />
            <WorldMap countriesData={countriesData} />
          </MapContainer>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
